.modal--small {
    width: 500px;
}

.modal--normal {
    width: 900px;
}

.modal-header {
    margin-top: 0;
}

.modal-header-title {
    font-size: fontSize("large");
    text-align: center;
    text-align: left;
    font-weight: 300;
    margin-top: 0;

    @media screen and (min-width: 1025px) {
        font-size: fontSize("larger");
    }
}

.modal-close {
    height: 32px;
    width: 32px;
    color: stencilColor("icon-color");
    font-size: 26px;
    line-height: remCalc($reveal-close-height);
    padding: 0;
    position: absolute;
    text-align: center;
    text-decoration: none;
    z-index: zIndex("high");

    @media screen and (min-width: 1025px) {
        font-size: 32px;
    }

    &:hover {
        color: stencilColor("icon-color-hover");
    }

    &.modal-close--button {
        position: static;
        width: auto;
        height: auto;
        font-size: 14px;
        //padding: $buttonSize-default-verticalPadding $buttonSize-default-horizontalPadding;
        padding: 0.75rem 0.75rem;
    }
}



