b2b-style.scss:
$b2bBorderColor: #a0a0a0;
$buttonSize-default-fontSize:           fontSize("base");
$buttonSize-default-verticalPadding:    spacing("third") + spacing("quarter");
$buttonSize-default-horizontalPadding:  spacing("single") + spacing("half");


// for foundry only
$colorRed: #ff0000;


.modal--small {
	right: auto;
}

.modal-close.modal-close--button {
    position: static;
    width: auto;
    height: auto;
    font-size: $buttonSize-default-fontSize;
    padding: $buttonSize-default-verticalPadding $buttonSize-default-horizontalPadding;
}

.account--fixed  {
	.navBar--account {
		@media screen and (min-width: 1025px) {
			margin-left: -80px;
			margin-right: -80px;
		}
	}
}

.b2b-loading-overlay {
	//display: none;
	position: fixed;
	background-color: rgba(0,0,0,0.46);
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: 999;

	img {
		position: absolute;
		top: 50%;
		left: 50%;
		width: 100px;
		height: 100px;
		margin-top: -50px;
		margin-left: -50px;
	}
}

.productView {
	position: relative;

	.product-loading-overlay {
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		background-color: rgba(255,255,255,0.99);

		img {
			position: absolute;
			top: 25%;
			left: 50%;
			width: 200px;
			height: 200px;
			margin-left: -100px;
		}

	}
}
.loading-span {
	display: block;
	width: 35px;
	height: 35px;
	margin: 10px auto;
	background-image: url('../img/loading.svg');
	background-position: center;
	background-repeat: no-repeat;
	background-size: contain;

}

.form-actions {
	text-align: left;
}

.b2b-wrap {
	font-size: 15px;

	.account-wrapper {
		font-size: 15px;
	}

	.button {
		//border-radius: 2px;

		&.square {
			width: 42px;
			padding-left: 0;
			padding-right: 0;
			text-align: center;
		}

	}
	.button--small {
		font-size: 13px;
		height: 30px;
		padding: 7px 10px; //(30 - 14 - 2)/2 = 7


		&.square {
			width: 30px;
			padding-left: 0;
			padding-right: 0;
			text-align: center;
		}
	}
	.b2b-column-wrap {
		margin: 0 -10px;

		&:after {
			display: block;
			content: "";
			clear: both;
			height: 0;
			visibility: hidden;
		}

		.b2b-column-left {
			
			padding: 0 10px;


			@media screen and (min-width: 1025px) {
				float: left;
				width: 40%;
			}
		}
		.b2b-column-right {
			padding: 0 10px;

			@media screen and (min-width: 1025px) {
				float: right;
				width: 60%;
			}
		}

	}

	button.action {
		color: stencilColor("color-blue");

		&:hover {
			text-decoration: underline;
		}
	}

	.form-label {
		font-weight: 600;
	}

	.form-label small {
		float: none;
		color: #e02b27;
	}

	.form-action {
		float: left;
		width: 100%;

		&.right {
			text-align: right;
		}
	}

	.title-wrap {
		position: relative;

		.page-title {
			display: inline-block;
			margin-top: 0;
			margin-bottom: 1rem;
			margin-right: 15px;
			font-size: 15px;
			font-weight: 400;

			@media screen and (min-width: 768px) {
				font-size: 25px;
				margin-bottom: 2rem;
			}
		}

		.page-title-small {
			margin-top: 0;
			margin-bottom: 2.5rem;
			color: #3d3c42;
			font-weight: 400;
			font-size: 25px;
			line-height: 1;
		}

		.page-title-desc {
			margin-bottom: 1rem;
		}

		.title-actions {
			
			margin-bottom: 2rem;

			@media screen and (min-width: 768px) {
				margin-top: 2rem;
				float: right;

			}
		}

		.rename-shopping-list {
			position: absolute;
			top: 15px;
			right: 0;
		}
	}

	.title-wrap--alt {
		margin-bottom: 3rem;

		.page-title {
			margin-bottom: 1rem;
		}

	}

	.actions-split {
		display: inline-block;
		margin: 0 8px;
		margin-top: 1px;
		color: #a6a6a6;
		font-size: 12px;
		vertical-align: top;
		height: 32px;
		line-height: 32px;
	}

	.action-link {
		text-decoration: none;
		display: inline-block;

		&:hover {
			text-decoration: underline;
		}
	}

	.action {
	    cursor: pointer;
	    // display: inline-block;
	    // font-size: 14px;
	    // line-height: 16px;
	    // font-weight: 600;
	    // padding: 8px 15px;
	    // border-radius: 3px;
	    // text-decoration: none;
	    // margin-right: 10px;
	    // background-color: #eee;
			// border: 1px solid #ccc;
      // color: #333;
      // box-shadow: inset 0 1px 0 0 #fff, inset 0 -1px 0 0 rgba(204,204,204,0.3);
				//
        // &:hover {
        // 	background-color: #ddd;
				//
        // }



	    &.fright {
	    	float: right;
	    	margin-right: 0;
	    }
	}

	// .action--primary {
	// 	background: #1979c3;
	//     border: 1px solid #1979c3;
	//     color: #fff;
	//     box-shadow: none;
	//
	// 	&:hover {
	//     	background: #006bb4;
  //           border: 1px solid #006bb4;
	//     }
	// }

	.form-wrap {
		margin-bottom: 20px;
		overflow: hidden;
		padding: 20px 24px 5px;
		border: 1px solid $b2bBorderColor;

		.form-label {
			font-weight: 700;
		}

		.form-label--mb {
			margin-bottom: 10px;
		}

		.form-field {
			width: 100%;
			float: left;

			&:last-child {
				margin-bottom: 0;
			}

			&.large {
				float: left;
				//width: 75%;
				width: calc(100% - 70px);

			}
			&.larger {
				float: left;
				width: calc(100% - 52px); 

			}

			&.small {
				float: left;
				width: 60px;
				margin-left: 10px;
			}
			&.smaller {
				float: left;
				width: 42px;
				margin-left: 10px;
			}

			&.form-field--split {
				float: left;
				margin-bottom: 1.25rem;
			}
		}

		.form-input {
			min-width: auto;
			width: 100%;
		}


		.button--search {
			padding-left: 0;
			padding-right: 0;
			height: 42px;
			width: 100%;
			font-size: 16px;
			text-align: center;
		}

		.form-input-desc {
			margin-top: 8px;
		}
	}

	.table-wrap {
		margin-bottom: 20px;
        border-bottom: 1px solid #e4e4e4;
	}

	table {
		width: 100%;
		border-collapse: collapse;
		border-spacing: 0;
		border: none;
		margin: 0;
	}
	table>thead {
		display: none;

		@media screen and (min-width: 768px) {
			display: table-header-group;
		}
	}

	table>tbody>tr {
		display: block;
		border-top: 1px solid #ccc;
		padding-top: 6px;

		@media screen and (min-width: 768px) {
			display: table-row;
			border-top: none;
			padding-top: 0;
		}

	}
	table>thead>tr>th,
	table>tbody>tr>td {
		text-align: left;
		padding: 5px 5px;
		border: none;

		@media screen and (min-width: 768px) {
			padding: 11px 10px;

			&.t-align-r {
				text-align: right;
			}
			&.t-align-c {
				text-align: center;
			}
		}

		

		&.actions-field {
			a {
				text-decoration: none;

				&:hover {
					text-decoration: underline;
				}
			}


			.button-item {
				display: inline-block;
				vertical-align: top;
				margin-left: 5px;
			}
		}
	}

	table>thead>tr>th {
		vertical-align: bottom;
		text-transform: none;
		font-weight: 700;
	}
	table>tbody>tr>td {
		
		display: block;

		.cell-line-name {
			font-size: 18px;
		}

		.mobile-td-lable {
			background-color: #e6e6e6;
			padding: 2px;
			border-radius: 3px;
			margin-right: 5px;
			font-size: 12px;
			font-style: italic;
			font-weight: 400;

		}

		@media screen and (min-width: 768px) {
			display: table-cell;
			border-top: 1px solid #ccc;
		    vertical-align: top;

		    .mobile-td-lable {
		    	display: none;
		    }

		}
	}

	.table-toolbar {
		margin-bottom: 30px;
		overflow: hidden;

		&.top {
			margin-bottom: 20px;
			clear: both;
		}

		.number-items {
			float: left;

			&+.action-links {
				float: right;

				.action-link,
				.action-label {
					margin-right: 0;
				}

				.action-link+.action-link,
				.action-link+.action-label {
					margin-left: 25px;
				}

				.action-label + .action-link {
					margin-left: 25px;
				}
			}
		}

		.action-links {
			.action-link,
			.action-label {
				//margin-right: 25px;
				margin-right: 5px;
			}

			.action-link {
				+.action-link {
					margin-left: 0;
				}
			}

			.action-label {
				cursor: pointer;
			}
		}

		.fright {
			float: right;
		}
	}

	.user-management-table {
		&.table-active-user {
			tbody tr[data-status=inactive] {
				display: none;
			}
		}
		&.table-inactive-user {
			tbody tr[data-status=active] {
				display: none;
			}
		}

		.col-actions {
			padding-right: 70px;
			text-align: right;
		}

		/* @media screen and (min-width: 768px) {
			>tbody>tr>td {
				vertical-align: middle;
			}
		} */
	}

	.shopping-list-detail {
		line-height: 1.8;
		margin-bottom: 1.5rem;
	}
	.shopping-list-status {
		select {
			width: 160px;
			display: inline-block;
		}
	}

	.shopping-lists-table {

		.actions-field {
	    	.button:nth-child(2) {
	    		margin-left: 0.5rem;
	    	}
	    }

		@media screen and (min-width: 768px) {
			>thead>tr>th,
		    >tbody>tr>td {
		    	&:first-child {
					width: 28%;
				}
				
		    }

			>thead>tr>th,
		    >tbody>tr>td {
		    	&:not(:first-child) {
					padding-left: 3%;
				}
				&:not(:last-child) {
					padding-right: 3%;
				}
		    }

		    

		}

		@media screen and (min-width: 1025px) {
			.actions-field {
		    	width: 205px;
		    }

		}
		

		&[css-status="40"] {
			tbody tr[data-status="30"],
			tbody tr[data-status="0"] {
				display: none;
			}
		}
		&[css-status="30"] {
			tbody tr[data-status="40"],
			tbody tr[data-status="0"] {
				display: none;
			}
		}
		&[css-status="0"] {
			tbody tr[data-status="30"],
			tbody tr[data-status="40"] {
				display: none;
			}
		}

	}

	.shopping-list-table {
		

		.mobile-td-lable {
			display: block;
			margin-right: 0;
			background-color: transparent;

			@media screen and (min-width: 1025px) {
				display: none;
			}
		}

		.t-align-r {
			text-align: right;
		}

		.label-unviable {
			display: block;
			color: $colorRed;
			text-transform: uppercase;
			font-weight: 400;
			font-style: normal;
			margin-top: 5px;
		}

		>thead>tr>th {
			font-weight: 400;
			padding-top: 18px;
			padding-bottom: 18px;
			vertical-align: middle;
			line-height: 16px;

			&.th-checkbox {
				position: relative;
				width: 40px;

				input {
					width: 16px;
					height: 16px;
				}
			}

			&.th-product-item {
				padding-left: 100px;
			}

			.button--removeall {
				position: absolute;
				left: 40px;
				bottom: 14px;
				white-space: nowrap;
				width: 90px;
				text-transform: none;
			}

			.button {
				margin-bottom: 0;
			}
		}

		>tbody>tr {
			font-size: 0;

			&:hover {
				background-color: #ebebeb;

				.action-lists {
					opacity: 1;
				}
			}

			&[data-in-catalog="false"] {
				background-color: #eee;
				opacity: 0.6;

				&:hover {
					background-color: #eee;

					.action-lists {
						opacity: 0;
					}
				}
			}
		}
		>tbody>tr>td {
			font-size: 1rem;
			padding-top: 20px;
		}

		td.col-checkbox {
			display: table-cell;
			padding-top: 43px;

			input {
				width: 16px;
				height: 16px;
				cursor: pointer;
			}
		}

		td.col-product-info {
			display: table-cell;
			width: 90%;
			font-size: 0;

			@media screen and (min-width: 768px) {
				width: 50%;
			}
		}

		td.col-product-price,td.col-product-qty, td.col-action {
			display: inline-block;
			width: 33.33%;
			vertical-align: top;

			@media screen and (min-width: 768px) {
				display: table-cell;
				width: auto;
			}
		}

		.col-product-qty {
			input {
				border: 1px solid #999;
			}
		}

		.product-iamge {
			position: relative;
			width: 65px;
			height: 65px;
			display: inline-block;
			margin-right: 25px;
			vertical-align: top;
			background-color: #fff;

			img {
				position: absolute;
				top: 0;
				left: 0;
				right: 0;
				bottom: 0;
				margin: auto;
				max-width: 65px;
				max-height: 65px;
			}
		}

		.product-description {
			display: inline-block;
			vertical-align: top;
			font-size: 14px;
			
			width: calc(100% - 90px);

			/* @media screen and (min-width: 768px) {
				padding-left: 15px;
			} */
		}

		.product-title {
			font-size: 14px;
			line-height: 1.2;
			margin-bottom: 5px;

			@media screen and (min-width: 768px) {
				line-height: 1.5;
				margin-bottom: 25px;
			}

			a {
				text-decoration: none;
				color: #333;

				&:hover {
					text-decoration: none;
				}
			}
		}


		.product-attribute {
			font-size: 12px;
			color: #111;


			span {
				font-weight: 700;
			}
		}

		.product-options {
			color: #333;
			span {
				//color: #999;
				font-weight: 700;
			}

		}

		.product-sku {
			margin-bottom: 2px;
		}

		.product-price,
		.product-subtotal {
			font-size: 18px;
			font-weight: 700;
		}

		.input-text {
			text-align: center;
			width: 54px;
			height: 32px;
			line-height: 32px;
		}

		.col-action {
			position: relative;
			padding-bottom: 55px;

			.action-lists {
				position: absolute;
				bottom: 11px;
				right: 10px;
				width: 200px;
				font-size: 0;
				

				@media screen and (min-width: 1025px) {
					opacity: 0;
				}

				.button {
					margin-bottom: 0;

					&+.button {
						margin-left: 5px;

					}
				}

				.button {
					margin-left: 5px;
				}

				 .action-icon {
				 	display: inline-block;
				 	cursor: pointer;
				 	color: #8f8f8f;
				 	font-size: 16px;
				 	text-decoration: none;

				 	&:hover {
				 		color: #333;
				 	}

				 	&+.action-icon {
				 		margin-left: 30px;
				 	}
				 }
			}
		}

		.no-option-value-tip {
			color: #ff0000;
			//font-size: 12px;
		}


	}

	#skus_search_results {
		position: relative;

		.product-qty-label {
			position: absolute;
			right: -52px;
			top: -25px;
		}
	}

	.search-product-table {
		position: relative;
		border: 1px solid #eee;

		tbody >tr {
			position: relative; 
		}
		
		tbody >tr>td {
			//border: none;
			//display: table-cell;
			border-top: none;
		}
		.col-checkbox {
			input {
				width: 16px;
				height: 16px;

			}
		}

		.col-product-figure,.col-product-info {
			display: table-cell;
		}
		.col-product-figure {
			width: 50px;

			@media screen and (min-width: 768px) {
				width: 70px;
			}

		}
		.col-product-price {
			position: absolute;
			top: 5px;
			right: 5px;

			@media screen and (min-width: 768px) {
				position: relative;
				top: auto;
				right: auto;
			}
		}
		.product-price {
			font-weight: 700;
		}

		.product-qty-col {
			width: 80px;
			position: absolute;
			right: -87px;
			top: 0;

			@media screen and (min-width: 768px) {
				top: auto;
			}

			input {
				width: 60px;
			}
		}

		.option-form {
			margin-top: 0.5rem;
			margin-bottom: 0;

			.form-label {
				margin-bottom: 5px;
			}
			.form-field {
				margin-bottom: 0.75rem;
			}
		}
	}

	#product_search_result_table {
		.product-qty-col {
			display: none;
		}
	}

	
}
