/* b2b sign up form start*/
.page-sidebar--ps {
	display: none;
	width: 217px;
	box-shadow: 0 0 8px -1px rgba(0,0,0,.25);
	margin: .71429rem .71429rem 0 0;
	background: #f6f6f7;

	@media screen and (min-width: 768px) {
		display: block;
	}

	.sidebar-content {
	    padding: 0 5px;

	    h2 {
		    font-size: 1.57143rem;
		    margin: 0 -1.07143rem .71429rem;
